import React from 'react'
import { useStore } from '../HelperComponents/StoreProvider'
import toast from 'react-hot-toast'

export default function Mobile() {
    const store = useStore()
    const products = [{
        id: 1,
        brand: "samsung",
        title: "Samsung S1 Pro Mobile",
        description: "The connected power of Samsung Galaxy",
        url: "data:image/jpeg;base64,",
        Price: 18000
    },
    {
        id: 2,
        brand: "Vivo",
        title: "Vivo y81 Mobile",
        description: "The connected power of vivo  Mobile",
        url: "data:image/jpeg;base64,",
        Price: 15000
    },
    {
        id: 3,
        brand: "Apple",
        title: "iphone 15 pro",
        url: "data:image/jpeg;base64,",
        description: "The connected power Apple iphone 15 pro",
        Price: 40000
    },
    {
        id: 4,
        brand: "oppo",
        title: "oppo A8",
        url: "data:image/jpeg;base64,",
        description: "The connected power of oppo moboiles A8",
        Price: 12000
    },
    {
        id: 5,
        brand: "One plus",
        title: "A19 plus",
        url: "data:image/jpeg;base64,",
        description: "The connected power of one plus Mobiles A8",
        Price: 25000
    },
    ]


    const AddToCart = (data) => {

        store.addProduct(data)
        toast.success("Product Added to Cart Successfully")
    }

    const removeFromCart = (id) => {
        store.removeProduct(id)
        toast.success("Product Removed Successfully")

    }
    return (
        <div>
            <div className='my-4 mx-5'>
                <h3 >Mobile Phones</h3>



                <div className='container'>
                    <div className='row'>
                        {products.map((item) => {
                            return (
                                <div className='col-md-3' key={item.id}>
                                    <div className="card " >
                                        <div className="card-body">
                                            <h4 className="card-title">{item.brand}</h4>
                                            <h6 className="card-title">{item.title}</h6>
                                            <img src={item?.url} />

                                            <p className="card-text">{item.description}</p>
                                            <h5>Rs.{item.Price}</h5>
                                            <button className="btn btn-danger mx-1">Buy Now</button>
                                            {store.cartData.some(e => e.id == item.id) ?
                                                <button className="btn btn-primary mx-1" onClick={() => removeFromCart(item.id)}>Remove</button>
                                                : <button className="btn btn-primary mx-1 " onClick={() => AddToCart(item)}>Add To Cart</button>
                                            }
                                        </div>
                                    </div>
                                </div>
                            )

                        })}

                    </div>

                </div>

            </div >

        </div>
    )
}
